function checkName(id){
    if(document.getElementById(id).value == ''){
        document.getElementById(id).style.borderColor = "#ced4da";
    }else{
        if(system.validUserName(document.getElementById(id).value)){
            document.getElementById(id).style.borderColor = "Green";
        }else{
            document.getElementById(id).style.borderColor = "Red";
        }
    }
}
function checkRange(id,max,min){
    if(document.getElementById(id).value == ''){
        document.getElementById(id).style.borderColor = "#ced4da";
    }else{
        if(system.validLimit([min,max],document.getElementById(id).value)){
            document.getElementById(id).style.borderColor = "Green";
        }else{
            document.getElementById(id).style.borderColor = "Red";
        }
    }
}
function checkOptions(id){
    if(document.getElementById(id).value == ''){
        document.getElementById(id).style.borderColor = "#ced4da";
    }else{
        let options = [];
        let a = document.getElementById(id);
        for(let i=0; i<a.childNodes.length; i++){
            if(a.childNodes[i].value != undefined){
                options.push(a.childNodes[i].value);
            }
        }
        if(system.validOptions(options,document.getElementById(id).value)){
            document.getElementById(id).style.borderColor = "Green";
        }else{
            document.getElementById(id).style.borderColor = "Red";
        }
    }
}
function checkAge(id,max,min){
    if(document.getElementById(id).value == ''){
        document.getElementById(id).style.borderColor = "#ced4da";
    }else{
        if(system.validLimit([min,max],system.dobToage(document.getElementById(id).value))){
            document.getElementById(id).style.borderColor = "Green";
        }else{
            document.getElementById(id).style.borderColor = "Red";
        }
    }
}

async function make_request_to_hire(data){
    try{
        const response = await fetch('/hire_details', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({ formData: data })
        });

        const result = await response.json();
        console.log(result);
    }catch(error){
        console.error("Request failed:", error);
    }
}